import ReactMarkdown from 'react-markdown';
import type { ChatMessage, ContentSegment } from '../types';
import { InlineQueryResult } from './InlineQueryResult';
import './MessageBubble.css';

function getThinkingSummary(text: string): string {
  const trimmed = text.trim();
  const firstLine = trimmed.split('\n')[0];
  return firstLine.length > 80 ? firstLine.slice(0, 80) + '...' : firstLine;
}

function SegmentView({ segment, isStreaming }: { segment: ContentSegment; isStreaming?: boolean }) {
  if (segment.type === 'tool' && segment.toolResult) {
    return (
      <div className="message-bubble__tool-segment">
        <InlineQueryResult result={segment.toolResult} />
      </div>
    );
  }

  if (segment.type === 'thinking' && segment.text?.trim()) {
    const summary = getThinkingSummary(segment.text);
    return (
      <details className="message-bubble__segment message-bubble__segment--thinking message-bubble__collapsible" open={isStreaming}>
        <summary className="message-bubble__collapsible-summary">
          <span className="message-bubble__segment-label">Thinking</span>
          <span className="message-bubble__collapsible-preview">{summary}</span>
        </summary>
        <div className="message-bubble__segment-content">
          <ReactMarkdown>{segment.text}</ReactMarkdown>
        </div>
      </details>
    );
  }

  if (segment.type === 'answer' && segment.text?.trim()) {
    return (
      <div className="message-bubble__segment message-bubble__segment--answer">
        <div className="message-bubble__segment-label message-bubble__segment-label--answer">Answer</div>
        <div className="message-bubble__segment-content">
          <ReactMarkdown>{segment.text}</ReactMarkdown>
        </div>
      </div>
    );
  }

  return null;
}

export function MessageBubble({ message }: { message: ChatMessage }) {
  const isUser = message.role === 'user';
  const hasSegments = !isUser && message.segments && message.segments.length > 0;

  return (
    <div className={`message-bubble message-bubble--${message.role}`}>
      <div className="message-bubble__header">
        {isUser ? 'You' : 'Assistant'}
      </div>

      {hasSegments ? (
        <div className="message-bubble__segments">
          {message.segments!.map((seg, i) => (
            <SegmentView key={i} segment={seg} />
          ))}
          {message.isStreaming && message.content && (() => {
            // Show text that's being streamed but not yet in a segment
            const segmentedText = message.segments!
              .filter((s) => s.type !== 'tool')
              .map((s) => s.text || '')
              .join('');
            const remaining = message.content.slice(segmentedText.length);
            if (remaining.trim()) {
              return (
                <div className="message-bubble__segment message-bubble__segment--thinking">
                  <div className="message-bubble__segment-label">Thinking</div>
                  <div className="message-bubble__segment-content">
                    <ReactMarkdown>{remaining}</ReactMarkdown>
                  </div>
                </div>
              );
            }
            return null;
          })()}
          {message.isStreaming && !message.content && (
            <span className="message-bubble__typing">Thinking...</span>
          )}
        </div>
      ) : (
        <>
          <div className="message-bubble__content">
            {message.content ? (
              <ReactMarkdown>{message.content}</ReactMarkdown>
            ) : message.isStreaming ? (
              <span className="message-bubble__typing">Thinking...</span>
            ) : null}
          </div>
          {message.toolCalls && message.toolCalls.length > 0 && (
            <div className="message-bubble__tools">
              {message.toolCalls.map((tc) => (
                <InlineQueryResult key={tc.toolCallId} result={tc} />
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

