/* Collapsible thinking segments */
.message-bubble__collapsible {
  cursor: default;
}

.message-bubble__collapsible-summary {
  display: flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  list-style: none;
  user-select: none;
}

.message-bubble__collapsible-summary::-webkit-details-marker {
  display: none;
}

.message-bubble__collapsible-summary::before {
  content: '▶';
  font-size: 9px;
  color: #9ca3af;
  transition: transform 0.15s ease;
  flex-shrink: 0;
}

.message-bubble__collapsible[open] > .message-bubble__collapsible-summary::before {
  transform: rotate(90deg);
}

.message-bubble__collapsible-summary .message-bubble__segment-label {
  margin-bottom: 0;
  flex-shrink: 0;
}

.message-bubble__collapsible-preview {
  font-size: 12px;
  color: #9ca3af;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  min-width: 0;
}

.message-bubble__collapsible[open] > .message-bubble__collapsible-summary .message-bubble__collapsible-preview {
  display: none;
}

.message-bubble__collapsible > .message-bubble__segment-content {
  margin-top: 6px;
}

.message-bubble__tool-segment {
  margin: 2px 0;
}

