import ReactMarkdown from 'react-markdown';
import type { ChatMessage, ContentSegment } from '../types';
import { InlineQueryResult } from './InlineQueryResult';
import './MessageBubble.css';

function getLastThinkingLine(text: string): string {
  const lines = text.trim().split('\n').filter((l) => l.trim());
  const last = lines[lines.length - 1] || '';
  return last.length > 100 ? last.slice(0, 100) + '...' : last;
}

function ThinkingBlock({ segments, streamingRemainder }: { segments: ContentSegment[]; streamingRemainder?: string }) {
  const thinkingTexts = segments
    .filter((s) => s.type === 'thinking' && s.text?.trim())
    .map((s) => s.text!);
  const allText = streamingRemainder
    ? [...thinkingTexts, streamingRemainder].join('\n\n')
    : thinkingTexts.join('\n\n');

  if (!allText.trim()) return null;

  const isActivelyStreaming = !!streamingRemainder;
  const summary = getLastThinkingLine(allText);

  return (
    <details className="message-bubble__segment message-bubble__segment--thinking message-bubble__collapsible" open={isActivelyStreaming || undefined}>
      <summary className="message-bubble__collapsible-summary">
        <span className="message-bubble__segment-label">Thinking</span>
        <span className="message-bubble__collapsible-preview">{summary}</span>
      </summary>
      <div className="message-bubble__segment-content">
        <ReactMarkdown>{allText}</ReactMarkdown>
      </div>
    </details>
  );
}

function NonThinkingSegment({ segment }: { segment: ContentSegment }) {
  if (segment.type === 'tool' && segment.toolResult) {
    return (
      <div className="message-bubble__tool-segment">
        <InlineQueryResult result={segment.toolResult} />
      </div>
    );
  }

  if (segment.type === 'answer' && segment.text?.trim()) {
    return (
